#!/bin/bash

# Script de despliegue flexible para n8n
# Uso: ./deploy.sh [opciones]
# Opciones:
#   -d, --domain DOMAIN     Dominio para el despliegue (default: n8n.autosystemprojects.site)
#   -p, --port PORT         Puerto interno del contenedor (default: 5678)
#   -e, --external PORT     Puerto externo del servidor (default: 8017)
#   -i, --image IMAGE       Imagen Docker a usar (default: ghcr.io/rhonmg2/n8n:latest)
#   -c, --container NAME    Nombre del contenedor (default: n8n-app)
#   --ssl                   Configurar SSL automáticamente
#   --no-nginx              No configurar nginx
#   --backup                Crear backup antes del despliegue
#   -h, --help              Mostrar ayuda

set -e

# Configuración por defecto
DEFAULT_DOMAIN="n8n.autosystemprojects.site"
DEFAULT_INTERNAL_PORT="5678"
DEFAULT_EXTERNAL_PORT="8017"
DEFAULT_IMAGE="ghcr.io/rhonmg2/n8n:latest"
DEFAULT_CONTAINER="n8n-app"

# Variables
DOMAIN="$DEFAULT_DOMAIN"
INTERNAL_PORT="$DEFAULT_INTERNAL_PORT"
EXTERNAL_PORT="$DEFAULT_EXTERNAL_PORT"
IMAGE="$DEFAULT_IMAGE"
CONTAINER_NAME="$DEFAULT_CONTAINER"
SETUP_SSL=false
SETUP_NGINX=true
CREATE_BACKUP=false

# Colores para output
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
BLUE='\033[0;34m'
NC='\033[0m' # No Color

# Función para mostrar ayuda
show_help() {
    echo "Script de despliegue flexible para n8n"
    echo ""
    echo "Uso: $0 [opciones]"
    echo ""
    echo "Opciones:"
    echo "  -d, --domain DOMAIN     Dominio para el despliegue (default: $DEFAULT_DOMAIN)"
    echo "  -p, --port PORT         Puerto interno del contenedor (default: $DEFAULT_INTERNAL_PORT)"
    echo "  -e, --external PORT     Puerto externo del servidor (default: $DEFAULT_EXTERNAL_PORT)"
    echo "  -i, --image IMAGE       Imagen Docker a usar (default: $DEFAULT_IMAGE)"
    echo "  -c, --container NAME    Nombre del contenedor (default: $DEFAULT_CONTAINER)"
    echo "  --ssl                   Configurar SSL automáticamente"
    echo "  --no-nginx              No configurar nginx"
    echo "  --backup                Crear backup antes del despliegue"
    echo "  -h, --help              Mostrar esta ayuda"
    echo ""
    echo "Ejemplos:"
    echo "  $0                                    # Despliegue básico"
    echo "  $0 --ssl                             # Despliegue con SSL"
    echo "  $0 -d mi.dominio.com -e 8080 --ssl  # Dominio y puerto personalizado con SSL"
    echo "  $0 --backup --ssl                    # Backup + despliegue + SSL"
}

# Función para logging
log() {
    echo -e "${BLUE}[$(date +'%Y-%m-%d %H:%M:%S')]${NC} $1"
}

log_success() {
    echo -e "${GREEN}[$(date +'%Y-%m-%d %H:%M:%S')] ✅${NC} $1"
}

log_warning() {
    echo -e "${YELLOW}[$(date +'%Y-%m-%d %H:%M:%S')] ⚠️${NC} $1"
}

log_error() {
    echo -e "${RED}[$(date +'%Y-%m-%d %H:%M:%S')] ❌${NC} $1"
}

# Parsear argumentos
while [[ $# -gt 0 ]]; do
    case $1 in
        -d|--domain)
            DOMAIN="$2"
            shift 2
            ;;
        -p|--port)
            INTERNAL_PORT="$2"
            shift 2
            ;;
        -e|--external)
            EXTERNAL_PORT="$2"
            shift 2
            ;;
        -i|--image)
            IMAGE="$2"
            shift 2
            ;;
        -c|--container)
            CONTAINER_NAME="$2"
            shift 2
            ;;
        --ssl)
            SETUP_SSL=true
            shift
            ;;
        --no-nginx)
            SETUP_NGINX=false
            shift
            ;;
        --backup)
            CREATE_BACKUP=true
            shift
            ;;
        -h|--help)
            show_help
            exit 0
            ;;
        *)
            log_error "Opción desconocida: $1"
            show_help
            exit 1
            ;;
    esac
done

# Mostrar configuración
